sp_widget.client_script:
function($scope, $interval) {
  /* widget controller */
  var c = this;
	$scope.stops = c.data.stops;
	$scope.stops.map(function(s) {
		s.obj = JSON.parse(s.value)
	})


	$interval(function(){
		c.server.update()
		.then(function(data){
			$scope.stops = c.data.stops;
	$scope.stops.map(function(s) {
		s.obj = JSON.parse(s.value)
	})
		});

	}, 7000)

}

sp_widget.css:
.trolley {
background-color: blue;
  padding: 5px;
  color: white;
}

.bus {
background-color: red;
  padding: 5px;
  color: white;
}

.tram {
background-color: orange;
  padding: 5px;
  color: white;
}

sp_widget.script:
(function() {
  /* populate the 'data' object */
  /* e.g., data.table = $sp.getValue('table'); */
data.stops = [];
	var stops = new GlideRecord('x_290873_skgt_boar_skgt_board_table');
	stops.addQuery('information', '!=', '{}');
	stops.orderBy('code');
	stops.query();
	while (stops.next()) {
		data.stops.push({value:stops.getValue('information')});
	}
})();
